import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import Home from './screens/Home';
import Login from './screens/Login';
import Signup from './screens/Signup';
import ProductDetail from './screens/ProductDetail';  // ✅ newly added
import OrderSuccess from './screens/OrderProduct';    // ✅ optional

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/product/:id" element={<ProductDetail />} />  {/* ✅ product detail route */}
        <Route path="/order-success" element={<OrderSuccess />} /> {/* ✅ optional confirmation */}
      </Routes>
    </Router>
  );
}

export default App;
