import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import Home from './screens/Home';
import Login from './screens/Login';
import Signup from './screens/Signup';
import ProductDetail from './screens/ProductDetail';
import OrderProduct from './screens/OrderProduct';       // ✅ correct component name
import OrderSuccess from './screens/OrderSuccess';       // ✅ success confirmation screen

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/product/:id" element={<ProductDetail />} />
        <Route path="/order/:id" element={<OrderProduct />} />      {/* ✅ order form for selected product */}
        <Route path="/order-success" element={<OrderSuccess />} />  {/* ✅ success screen after confirmation */}
      </Routes>
    </Router>
  );
}

export default App;
