import React from 'react';
import { TrendingUp, TrendingDown } from 'lucide-react';
import Card from './Card';
import '../styles/App.css';

const StatCard = ({ title, value, subtitle, icon: Icon, trend, color = 'blue' }) => {
  return (
    <Card className={`stat-card ${color}`}>
      <div className="stat-header">
        <div>
          <p className="stat-title">{title}</p>
          <p className="stat-value">{value}</p>
          {subtitle && <p className="stat-subtitle">{subtitle}</p>}
        </div>
        <div className="stat-icon"><Icon size={24} /></div>
      </div>
      {trend !== undefined && (
        <div className="stat-trend">
          {trend >= 0 ? <TrendingUp className="trend-icon green" /> : <TrendingDown className="trend-icon red" />}
          <span className={`trend-value ${trend >= 0 ? 'green' : 'red'}`}>{Math.abs(trend)}%</span>
          <span className="trend-subtext"> vs last month</span>
        </div>
      )}
    </Card>
  );
};

export default StatCard;
