import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

import Home from './screens/Home';
import Login from './screens/Login';
import Signup from './screens/Signup';
import ProductDetail from './screens/ProductDetail';
import OrderProduct from './screens/OrderProduct';

const App = () => {
  const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';

  return (
    <Router>
      <Routes>
        {/* Login and Signup accessible always */}
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />

        {/* All other routes require login */}
        <Route path="/" element={isLoggedIn ? <Home /> : <Navigate to="/login" />} />
        <Route path="/product/:id" element={isLoggedIn ? <ProductDetail /> : <Navigate to="/login" />} />
        <Route path="/order/:id" element={isLoggedIn ? <OrderProduct /> : <Navigate to="/login" />} />

        {/* Fallback to login if route not found */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default App;
