import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

import Home from './screens/Home';
import Login from './screens/Login';
import Signup from './screens/Signup';
import ProductDetail from './screens/ProductDetail';
import OrderProduct from './screens/OrderProduct';
import OrderSuccess from './screens/OrderSuccess';

const App = () => {
  const isLoggedIn = localStorage.getItem('isLoggedIn') === 'true';

  return (
    <Router>
      <Routes>
        {/* Show login/signup first */}
        <Route
          path="/login"
          element={isLoggedIn ? <Navigate to="/" /> : <Login />}
        />
        <Route
          path="/signup"
          element={isLoggedIn ? <Navigate to="/" /> : <Signup />}
        />

        {/* Protect rest of the routes */}
        <Route
          path="/"
          element={isLoggedIn ? <Home /> : <Navigate to="/login" />}
        />
        <Route
          path="/product/:id"
          element={isLoggedIn ? <ProductDetail /> : <Navigate to="/login" />}
        />
        <Route
          path="/order/:id"
          element={isLoggedIn ? <OrderProduct /> : <Navigate to="/login" />}
        />
        <Route
          path="/order-success"
          element={isLoggedIn ? <OrderSuccess /> : <Navigate to="/login" />}
        />

        {/* Fallback to login */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default App;
