import React from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { ArrowLeft, Star, Heart, Share2, ShoppingCart, Truck, Shield, RotateCcw } from 'lucide-react';
import './ProductDetail.css';

import { products } from '../data/products'; // Assuming you keep all 11 products in a separate file

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const product = products.find(p => p.id === parseInt(id || '0'));

  if (!product) {
    return (
      <div className="not-found">
        <h2>Product not found</h2>
        <Link to="/">Return to Home</Link>
      </div>
    );
  }

  const handleOrderNow = () => {
    navigate('/order-success');
  };

  const discount = Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100);

  return (
    <div className="product-detail">
      <header className="header">
        <button onClick={() => navigate('/')} className="back-button">
          <ArrowLeft size={20} />
          Back to Products
        </button>
      </header>

      <main className="product-content">
        <div className="image-section">
          <img src={product.image} alt={product.name} />
        </div>

        <div className="info-section">
          <span className="category">{product.category}</span>
          <h1>{product.name}</h1>

          <div className="rating">
            <Star size={20} className="star" />
            <span>{product.rating}</span>
            <span>({product.reviews} reviews)</span>
            {product.inStock && <span className="in-stock">In Stock</span>}
          </div>

          <div className="price-section">
            <span className="price">${product.price}</span>
            <span className="original">${product.originalPrice}</span>
            <span className="discount">{discount}% OFF</span>
          </div>

          <div className="description">
            <h3>Description</h3>
            <p>{product.description}</p>
          </div>

          <div className="features">
            <h3>Key Features</h3>
            <ul>
              {product.features.map((feature, index) => (
                <li key={index}>• {feature}</li>
              ))}
            </ul>
          </div>

          <div className="actions">
            <button className="order-btn" onClick={handleOrderNow}>
              <ShoppingCart size={20} />
              Order Now - ${product.price}
            </button>
            <div className="extra-buttons">
              <button><Heart size={20} /> Wishlist</button>
              <button><Share2 size={20} /> Share</button>
            </div>
          </div>

          <div className="shipping-info">
            <p><Truck size={18} /> Free shipping on orders over $100</p>
            <p><Shield size={18} /> 2-year warranty included</p>
            <p><RotateCcw size={18} /> 30-day return policy</p>
          </div>
        </div>
      </main>
    </div>
  );
};

export default ProductDetail;
